import { useEffect, useState } from "react";
import { data, result, pag } from "../../share/data";
import styles from "./App.module.css";
const listPerPage = 6;

function TableRow({ item, index, showShop, openShop }) {
  const res = result(item.shop);
  return (
    <div
      onClick={() => showShop(item.name)}
      key={`${item.name}${item.articule}`}
      className={styles.item}
    >
      <p>{index + 1}</p>
      <p>{item.name}</p>
      <p>{item.articule}</p>
      <p>{res.count}</p>
      <p>{res.avg}</p>
      <p>{res.sum}</p>
    </div>
  );
}

function ShopSubTable({ item, openShop }) {
  return (
    <div
      key={item.name}
      className={`${styles.shop} ${
        item.name === openShop ? styles.active : null
      }`}
    >
      {item.shop.map((el, i) => (
        <div key={i + 1} className={styles.item}>
          <p>{i + 1}</p>
          <p>{el.shop_name}</p>
          <p>{el.count}</p>
          <p>{el.price}</p>
          <p>{(el.price * el.count).toFixed(2)}</p>
        </div>
      ))}
    </div>
  );
}

function Pagination({ changePage, activePage }) {
  return (
    <div className={styles.pag}>
      {pag(listPerPage).map((item) => (
        <span
          onClick={() => changePage(item)}
          className={`${activePage === item ? styles.active : null}`}
          key={item}
        >
          {item}
        </span>
      ))}
    </div>
  );
}

export default function App() {
  const [startindex, setStartIndex] = useState(0);
  const [list, setList] = useState(
    data.slice(startindex, startindex + listPerPage)
  );
  const [activePage, setActivePage] = useState(1);
  useEffect(() => {
    setList(data.slice(startindex, startindex + listPerPage));
  }, [startindex]);
  const changePage = (item) => {
    setStartIndex((item - 1) * listPerPage);
    setActivePage(item);
  };
  const [openShop, setOpenShop] = useState({ isOpen: false, productName: "" });
  const showShop = (name) => {
    setOpenShop((prev) => {
      if (prev.productName !== name) {
        return {
          isOpen: true,
          productName: name
        };
      } else {
        return {
          isOpen: !prev.isOpen,
          productName: name
        };
      }
    });
  };

  return (
    <div className="App">
      <div className={styles.wrap}>
        <div key="abcde" className={`${styles.item} ${styles.itemTitle}`}>
          <p>#</p>
          <p>Name</p>
          <p>Articule</p>
          <p>Count</p>
          <p>AVG Price</p>
          <p>Sum</p>
        </div>
        {list.map((item, i) => (
          <>
            <TableRow
              item={item}
              index={startindex + i}
              showShop={showShop}
              openShop={openShop.productName}
            />
            <ShopSubTable item={item} openShop={openShop.productName} />
          </>
        ))}
      </div>
      <Pagination changePage={changePage} activePage={activePage} />
    </div>
  );
}
